// src/pages/Dashboard.js
import React from 'react';
import { Link, useNavigate } from 'react-router-dom';
import './Dashboard.css'; // New CSS file for the dashboard
import collegeLogo from '../assets/logo.png'; // Your college logo

// Assuming you have these images for the cards, replace with your actual paths
import keysImage from '../assets/keys_found.png'; // Example image for 'Found Items' card
import boxImage from '../assets/lost_box.png'; // Example image for 'Lost Items' card

const Dashboard = () => {
    const navigate = useNavigate();

    const handleSearchClick = () => {
        navigate('/search-item'); // Assuming a route for searching items
    };

    const handleReportClick = () => {
        navigate('/report-item'); // Assuming a route for reporting lost items
    };

    const handleLogout = () => {
        localStorage.removeItem('token'); // Clear token
        navigate('/'); // Redirect to login page
    };

    return (
        <div className="dashboard-container">
            {/* Top Navigation Bar */}
            <nav className="top-navbar">
                <div className="navbar-brand">
                    <img src={collegeLogo} alt="College Logo" className="college-logo" />
                </div>
                <div className="navbar-links">
                    <Link to="/home" className="nav-link">HOME</Link>
                    <Link to="/lost-items" className="nav-link">LOST ITEMS</Link>
                    <Link to="/found-items" className="nav-link">FOUND ITEMS</Link>
                    <button onClick={handleLogout} className="nav-link logout-btn">LOGOUT</button>
                </div>
            </nav>

            {/* Main Content Area */}
            <main className="dashboard-main-content">
                <div className="hero-section">
                    <h1 className="hero-title">Find & Recover <br /><span className="highlight-text">With Ease</span></h1>
                    <p className="hero-subtitle">
                        Experience effortless searching & reporting for lost and found items.
                    </p>
                    <div className="hero-actions">
                        <button onClick={handleSearchClick} className="btn btn-primary">
                            Search an Item
                        </button>
                        <button onClick={handleReportClick} className="btn btn-secondary">
                            Report Lost Item
                        </button>
                    </div>
                </div>

                <div className="feature-cards-section">
                    <div className="feature-card">
                        <img src={keysImage} alt="Found Keys" className="card-icon" />
                        <p className="card-text">
                            A dedicated place for people to look for and reclaim their misplaced possessions.
                        </p>
                    </div>
                    <div className="feature-card">
                        <img src={boxImage} alt="Lost & Found Box" className="card-icon" />
                        <p className="card-text">
                            Enhances security by providing a secure, accountable process for handling items.
                        </p>
                    </div>
                </div>
            </main>

            {/* Footer */}
            <footer className="dashboard-footer">
                <p>&copy; 2025 Lost & Found Portal | All Rights Reserved</p>
            </footer>
        </div>
    );
};

export default Dashboard;
